const deleteItemMutation = useMutation({
  mutationFn: deleteItemAction,
  onMutate: async (itemId) => {
    // Cancel outgoing refetches
    await queryClient.cancelQueries({ queryKey: ['hierarchy'] })

    // Snapshot current state
    const previousData = queryClient.getQueryData(['hierarchy'])

    // Optimistically remove item from cache
    queryClient.setQueryData(['hierarchy'], (old) => {
      return removeItemFromHierarchy(old, itemId)
