    return { previousData }
  },
  onError: (err, itemId, context) => {
    // Rollback on error
    queryClient.setQueryData(['hierarchy'], context.previousData)
    toast.error('–ù–µ —É–¥–∞–ª–æ—Å—å —É–¥–∞–ª–∏—Ç—å —ç–ª–µ–º–µ–Ω—Ç')
  },
  onSuccess: () => {
