```typescript
const createSectionMutation = useMutation({
  mutationFn: createSectionAction,
  onMutate: async (newSectionData) => {
    await queryClient.cancelQueries({ queryKey: ['hierarchy'] })

    const previousData = queryClient.getQueryData(['hierarchy'])

    // –í—ã—á–∏—Å–ª–∏—Ç—å –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π display_order
    const existingSections = previousData.sections || []
    const maxOrder = Math.max(...existingSections.map(s => s.display_order), 0)

    const optimisticSection = {
      ...newSectionData,
      section_id: `temp-${Date.now()}`,
      display_order: maxOrder + 1, // –∏–ª–∏ –≤—Å—Ç–∞–≤–∏—Ç—å –º–µ–∂–¥—É —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–º–∏
      created_at: new Date().toISOString(),
    }

    queryClient.setQueryData(['hierarchy'], (old) => {
      const updated = {
        ...old,
        sections: [...old.sections, optimisticSection]
      }

      // –í–ê–ñ–ù–û: –û—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞—Ç—å –º–∞—Å—Å–∏–≤ –ø–µ—Ä–µ–¥ –≤–æ–∑–≤—Ä–∞—Ç–æ–º!
      updated.sections.sort((a, b) => a.display_order - b.display_order)

      return updated
    })

    return { previousData }
  },
  onError: (err, newSection, context) => {
    queryClient.setQueryData(['hierarchy'], context.previousData)
    toast.error('–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å —Ä–∞–∑–¥–µ–ª')
  },
  onSuccess: (serverSection, variables, context) => {
    // –ó–∞–º–µ–Ω–∏—Ç—å –≤—Ä–µ–º–µ–Ω–Ω—ã–π ID –Ω–∞ —Ä–µ–∞–ª—å–Ω—ã–π
    queryClient.setQueryData(['hierarchy'], (old) => {
      return {
        ...old,
        sections: old.sections.map(s => 
          s.section_id.startsWith('temp-') ? serverSection : s
        )
      }
    })
  },
})
