```typescript
const filterHierarchy = (nodes: Node[], projectName: string): Node[] => {
  const normalizedFilter = projectName.toLowerCase().trim()

  return nodes
    .map(node => {
      // –ü—Ä–æ–≤–µ—Ä–∏—Ç—å —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ
      const matches = node.project_name?.toLowerCase().includes(normalizedFilter)

      // –†–µ–∫—É—Ä—Å–∏–≤–Ω–æ —Ñ–∏–ª—å—Ç—Ä–æ–≤–∞—Ç—å –¥–µ—Ç–µ–π
      const filteredChildren = node.children 
        ? filterHierarchy(node.children, projectName) 
        : []

      // –û—Å—Ç–∞–≤–∏—Ç—å —É–∑–µ–ª –µ—Å–ª–∏ –æ–Ω —Å–∞–º —Å–æ–≤–ø–∞–¥–∞–µ—Ç –ò–õ–ò –µ—Å—Ç—å —Å–æ–≤–ø–∞–≤—à–∏–µ –¥–µ—Ç–∏
      if (matches || filteredChildren.length > 0) {
        return { ...node, children: filteredChildren }
      }

      return null
    })
    .filter(Boolean)
}
```

